import React, { useState } from "react" ; 

const Taskcontext = React.createContext;

export default Taskcontext ; 

export function UpdateTask({children})
{
    const[taskname, updatetaskname] = useState("");
    const[taskdes,updatetaskdes] = useState("");
    const[time,updatetime] = useState('');

    return <Taskcontext.provider value={{taskname,taskdes,time,updatetaskname,updatetaskdes,updatetime}}>{children}</Taskcontext.provider>
}
