import React, { useState } from "react";
import { Button,Modal } from 'react-bootstrap';


export default function Task()
{   const[taskname,updatetaskname] = useState("Hello");
    const [modalShow, setModalShow] = React.useState(false);
    return<> 
    <div class="container-fluid">
        <div class="row col-12  taskheader">
           <span>To do list - date</span>
        </div>
        <button class="addbutton" onClick={() => setModalShow(true)}><i class="fas fa-plus-circle fa-2x"></i></button>
        <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
        tname={taskname,updatetaskname}
      />
      {taskname}
    </div>
     <div>
     
   </div>
</>
}

function MyVerticallyCenteredModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Task
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form onSubmit={
          (e)=>{
              e.preventDefault();
              console.log(props.tname);
          }
        }>
        <div class="mb-3">
     <label for="taskname" class="form-label">Task Name</label>
      <input type="text" class="form-control" id="taskname" />
     </div>
     <div class="mb-3">
     <label for="description" class="form-label">Description</label>
     <textarea class="form-control" id="description" rows="2"></textarea>
     </div>
     <div class="container-fluid row col-4">
     <label for="time" class="form-label">Time-(HH:mm-AM/PM)</label>
     <input type="time" class="form-control form-sm" min="00:00" max="23:59" id="time"  />
     </div>
        <Modal.Footer>
        <button class="btn btn-primary" type="submit">Submit</button></Modal.Footer>
        </form>
      </Modal.Body>
    </Modal>
  );
}
