import React, { useState } from "react";
import Background from "./img/1489353.jpg";
import "./App.css";



export default function Login()
{    
    const[type,updatetype] = useState("password");
   
    return <div style={{
        backgroundImage: `url(${Background})`,
        backgroundSize: "cover",
        height: "100vh",
        color: "#f5f5f5"
      }}
     class="container-fluid row">
        <div class="col-4 bg-primary">
          <form>
          <div class="container-fluid row">
              <div class="col-11">
          <label for="exampleFormControlInput1" class="form-label">Email address</label>
          <input type="email" class="form-control" id="exampleFormControlInput1" placeholder="name@example.com" required/>
          </div></div><br/>
          <div class="container-fluid row ">
              <div class="col-11">
              <label for="password" class="form-label">Password</label>
               <input type={type} class="form-control" id="password" required/>
              </div>
              <div class="co1-11">
               <input type="checkbox" id="showpassword" name="showpassword" value="password" onChange={
                   ()=>{
                     if(type=="password")
                     {
                         updatetype("text")
                     }
                     else
                     {
                        updatetype("password")
                     }
                   }
               }/>
               <label for="showpassword">&nbsp; Show Password</label><br/>
              </div>
          </div><br/>
          <div class="container-fluid row ">
              <div class="col-11">
            Forget Password</div>
          </div>
          <div class="container-fluid row ">
          <div class="col-11">
          <button type="submit" class="btn btn-primary">Login</button>
           </div>
          </div>
          </form>
        </div>
        </div>
}
