import React, { useContext, useEffect, useState } from "react";
import { Button,Modal } from 'react-bootstrap';
import { useParams } from "react-router"
import { Addtask, Deletetask, Gettask } from "./api";
import TaskContext from "./taskcontext";





export default function Task()
{   
    let task = useContext(TaskContext);
    let {id} = useParams();
    const[tasklist,updatetasklist] = useState([]);
    const [modalShow, setModalShow] = React.useState(false);
    const [editmodalShow, seteditModalShow] = React.useState(false);
    let arr = id.split("-");
    let monthname = arr[1];
    arr[1] =convertToNum(monthname);
    useEffect( async()=>{
      task.updateuserdata([...arr]); 
      let obj = {
        date : +arr[0],
        month: arr[1]
      }
      let response = await Gettask(arr[2],obj)
      updatetasklist([...response.data]);
      
    },[])
    useEffect( async()=>{
      task.updateuserdata([...arr]); 
      let obj = {
        date : +arr[0],
        month: arr[1]
      }
      let response = await Gettask(arr[2],obj)
      updatetasklist([...response.data]);
      
    },[modalShow])
    return<> 
    <div class="container-fluid bg-primary">
        <div class="row col-12  taskheader">
           <span>To do list on {`${arr[0]}-${monthname}`}</span>
        </div>
        <button class="addbutton btn btn-primary" onClick={() => setModalShow(true)}><i class="fas fa-plus-circle fa-2x"></i></button>
        <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
        id={id}
      />
        
    </div>
    
    <table class="table task">
    <thead>
    <tr class="task">
      <th class="task time" scope="col">Time</th>
      <th class="task name" scope="col">Task Name</th>
      <th class="task des" scope="col">Description</th>
      <th class="task time" scope="col">Status</th>
      <th class="task action" scope="col">Action</th>
    </tr> 
  </thead>
  <tbody>{
    tasklist.map((i)=>{

    return <tr class="task ">
      <td class="task taskdata">{i.TaskTime}</td>
      <td class="task taskdata">{i.Taskname}</td>
      <td class="task taskdata">{i.Taskdescription}</td>
      {
      i.Completed==false?<td class="task taskdata"style={{color:`red`}}>waiting</td>:<td class="task taskdata"style={{color:`Green`}}>done</td>
       }
      <td class="task taskdata"><button class="btn btn-danger bigger" onClick={
        async()=>{
          await Deletetask(i._id);
          let obj = {
            date : +arr[0],
            month: arr[1]
          }
          let response = await Gettask(arr[2],obj)
          updatetasklist([...response.data]);
        }
      }><i class="far fa-calendar-times"></i></button><button class="btn btn-warning bigger" onClick={
        ()=>{seteditModalShow(true)}
      }><i class="far fa-edit"></i><MyVerticallyCenteredEditModal
      show={editmodalShow}
      onHide={() => seteditModalShow(false)}
      data={i}
    /></button></td>
    </tr>
    })}
  </tbody>
</table>

</>
}

