export default function Calender()
{   
    let date = new Date();
    let[startday,updatestartday]=useState("");
    let[monthlength,updatemonthlength]=useState(0);
    useEffect(()=>{
     var firstday = new Date(date.getFullYear(),date.getMonth(),1).toString().split(" ")[0];
     var lastday = new Date(date.getFullYear(),date.getMonth()+1,0).toString().split(" ")[2];   
     updatestartday(findStartDay(firstday));
     updatemonthlength(+lastday);
    },[])
    let[month,updatemonth] = useState(convertToMonth(date.getMonth()+1))
    return <>
    <div class="header">
     <span class="monthname">{month}</span>
    </div>
    <table class="table">
    <thead>
      <tr>
        <th scope="col">Sun</th>
        <th scope="col">Mon</th>
