import React, { useState } from "react";

export default function Calender()
{   
    let date = new Date();
    let[month,updatemonth] = useState(convertToMonth(date.getMonth()+1))
    return <>
    <div class="header">
     <span class="monthname">{month}</span>
    </div>
    <table class="table">
    <thead>
      <tr>
        <th scope="col">Sun</th>
        <th scope="col">Mon</th>
        <th scope="col">Tue</th>
        <th scope="col">Wed</th>
        <th scope="col">Thu</th>
        <th scope="col">Fri</th>
        <th scope="col">Sat</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <td>1</td>
        <td>2</td>
        <td>3</td>
      </tr>
    </tbody>
  </table></>
}

function convertToMonth(num)
{
  switch(num)
  {
      case 1 : 
      {
          return "January";
      }
      case 2 : 
      {
          return "Febuary";
      }
      case 3 :
      {
          return "March"
      }
      case 4 :
      {
          return "April"
      }
      case 5 :
      {
            return "May"
      }
      case 6 :
        {
            return "June"
        }
      case 7:
        {
            return "July"
        }
        case 8:
        {
            return "August"
        } 
        case 9:
        {
            return "September"
        } 
        case 10:
        {
            return "October"
        } 
        case 11:
        {
            return "November"
        } 
        case 12:
        {
            return "December"
        } 
  }
}
