import React, { useEffect, useState } from "react";

export default function Calender()
{   
    let date = new Date();
    let[startday,updatestartday]=useState("");
    let[monthlength,updatemonthlength]=useState(0);
    let[firstline,updatefirstline] = useState([]);
    let[nextline,updatenextline] = useState([]);
    useEffect(()=>{
     var firstday = new Date(date.getFullYear(),date.getMonth(),1).toString().split(" ")[0];
     var lastday = new Date(date.getFullYear(),date.getMonth()+1,0).toString().split(" ")[2];   
     updatestartday(findStartDay(firstday));
     updatemonthlength(+lastday);
     updatefirstline([...makeFirstLine(findStartDay(firstday))]);
     updatenextline([...makeNextLine(findStartDay(firstday),+lastday)]);
     
    },[])
    let[month,updatemonth] = useState(convertToMonth(date.getMonth()+1))
    return <>
     <div class="container-fluid ">
     <div class="row">
     <div class="offset-1 col-1 sidebutton"><span class="alignbutton"><button class="btn">&#60;&#60;</button></span></div>
     <div class="col-8 header"><span class="monthname">{month}</span></div>   
     <div class="col-1 sidebutton"><span class="alignbutton"><button class="btn">&#62;&#62;</button></span></div>
     <div class="col-1 sidebutton"><button class="btn">Logout</button></div>
    </div>
    </div>
    <div class="container-fluid row offset-1 col-10">
    <table class="table">
    <thead>
      <tr>
        <th scope="col">Sun</th>
        <th scope="col">Mon</th>
        <th scope="col">Tue</th>
        <th scope="col">Wed</th>
        <th scope="col">Thu</th>
        <th scope="col">Fri</th>
        <th scope="col">Sat</th>
      </tr>
    </thead>
    <tbody>
     {  <tr>{ 
        firstline.map((i)=>{
         return <td>{i}</td>
        })}</tr>
     }
     {
         nextline.map((i)=>{
             return <tr>{i.map((j)=>{
                 return <td>{j}</td>
             })}</tr>
         })
     }
    </tbody>
  </table>
  </div></>
}

function convertToMonth(num)
{
  switch(num)
  {
      case 1 : 
      {
          return "January";
      }
      case 2 : 
      {
          return "Febuary";
      }
      case 3 :
      {
          return "March"
      }
      case 4 :
      {
          return "April"
      }
      case 5 :
      {
            return "May"
      }
      case 6 :
        {
            return "June"
        }
      case 7:
        {
            return "July"
        }
        case 8:
        {
            return "August"
        } 
        case 9:
        {
            return "September"
        } 
        case 10:
        {
            return "October"
        } 
        case 11:
        {
            return "November"
        } 
        case 12:
        {
            return "December"
        } 
  }
}
function findStartDay(day)
{
    switch(day)
    {
        case "sun": return 0 ; 
        case "Mon": return 1 ; 
        case "Tue": return 2 ; 
        case "Wed": return 3 ; 
        case "Thu": return 4 ; 
        case "Fri": return 5 ; 
        case "Sat": return 6 ; 
    }
}

function makeFirstLine(day)
{
  let arr = [];
  let i = 1 
  for(; i <=day;i++)
  {
      arr.push(" ");
  }
  for(let j = 1 ; i<=7;i++)
  {
      arr.push(j)
  }
  return arr;
}
function makeNextLine(day,length)
{
  let filleddays = 7 - day; 
  let arr = [] ; 
  let remainingdays = length -filleddays;
  let start = filleddays+1;
  
  if(remainingdays%7==0)
  {
      remainingdays/=7;
  }
  else
  {
      remainingdays= Math.floor(remainingdays/7);
      remainingdays++;
  }
  for(let i = 0 ; i<remainingdays;i++)
  {
      let temp = [];
      for(let j =0 ;j<7 && start<=length;j++,start++)
      {   
          
          temp.push(start)
      }
      arr.push(temp);
  } 
  return arr;
}
