import React, { useContext, useEffect, useState } from "react";
import { Button,Modal } from 'react-bootstrap';
import { useParams } from "react-router"
import { Addtask } from "./api";
import TaskContext from "./taskcontext";





export default function Task()
{   
    let task = useContext(TaskContext);
    let {id} = useParams();
    const [modalShow, setModalShow] = React.useState(false);
    let arr = id.split("-");
    let monthname = arr[1];
    arr[1] =convertToNum(monthname);
    useEffect(()=>{
      task.updateuserdata([...arr]);  
    },[])
    return<> 
    <div class="container-fluid bg-primary">
        <div class="row col-12  taskheader">
           <span>To do list on {`${arr[0]}-${monthname}`}</span>
        </div>
        <button class="addbutton btn btn-primary" onClick={() => setModalShow(true)}><i class="fas fa-plus-circle fa-2x"></i></button>
        <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
        id={id}
      />
    
    </div>
    <div class="container-fluid">
     <div class="row task-1">
      <div className="col-1">
      <div class="card">
     <div class="card-body">
     
     </div>
       </div>
        </div>
        <div className="col-3">  <div class="card">
     <div class="card-body">
     
     </div>
       </div>
     </div> 
     <div className="col-6"> <div class="card">
     <div class="card-body">
     
     </div>
       </div>
       </div>
       <div className="col-1"> <div class="card bigger">
     <div class="card-body">
     
     </div>
       </div>

       </div>
       <div className="col-1"> <div class="card bigger">
     <div class="card-body">
     
     </div>
       </div>
         
       </div>
   </div>
   </div>
</>
}

function MyVerticallyCenteredModal(props) {
  let task = useContext(TaskContext);
  const[tname,updatetname] = useState("");
  const[tdes,updatetdes] = useState("");
  const[tt,updatett] = useState(""); 
  const[message,updatemessage] = useState("");
  return (<Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Task
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form onSubmit={
         async (e)=>{
              e.preventDefault();
              let obj = 
              {
                Taskname :tname,
                Taskdescription:tdes,
                TaskTime:tt,
                Taskmonth: task.userdata[1],
                Taskdate : task.userdata[0],
                Userid : task.userdata[2]
              }
              console.log(obj)
              //await Addtask(obj);
              updatetname("");
              updatett("");
              updatetdes("");
              props.onHide();
          }
        }>
        <div class="container-fluid row col-12">
     <label for="taskname" class="form-label">Task Name</label>
      <input type="text" class="form-control" id="taskname" required value={tname} onChange={
        (e)=>{updatetname(e.target.value)}
      } />
     </div><br/>
     <div class="container-fluid row col-12">
     <label for="description" class="form-label">Description</label>
     <textarea class="form-control" id="description" rows="2" required value={tdes} onChange={(e)=>{updatetdes(e.target.value)}}></textarea>
     </div><br/>
     <div class="container-fluid row  col-4">
     <label for="time" class="form-label">Time-(HH:mm-AM/PM)</label>
     <input type="time" class="form-control form-sm" min="00:00" max="23:59" id="time" required value={tt} onChange={
       (e)=>{updatett(e.target.value)}
     } />
     </div><br/>
     <div class="container-fluid row  col-12">
     {message}
     </div><br/>
        <Modal.Footer>
        <button class="btn btn-primary" type="submit">Submit</button></Modal.Footer>
        </form>
      </Modal.Body>
    </Modal>
  );
}
function convertToNum(Month)
{
  switch(Month)
  {
      case "January" : 
      {
          return 0;
      }
      case "Febuary" : 
      {
          return 1 ;
      }
      case "March" :
      {
          return 2
      }
      case "April" :
      {
          return 3
      }
      case "May" :
      {
          return 4
      }
      case "June" :
        {
            return 5
        }
      case "July":
        {
            return 6
        }
        case "August":
        {
            return 7
        } 
        case "September":
        {
            return 8
        } 
        case "October":
        {
            return 9
        } 
        case "November":
        {
            return 10
        } 
        case "December":
        {
            return 11
        } 
  }
}
