import React, { useState } from "react";
import { Button,Modal } from 'react-bootstrap';
import { useParams } from "react-router";


export default function Task()
{   let {id} = useParams();
    const [modalShow, setModalShow] = React.useState(false);
    let arr = id.split("-");
    console.log(arr);
    return<> 
    <div class="container-fluid bg-primary">
        <div class="row col-12  taskheader">
           <span>To do list on {`${arr[0]}-${arr[1]}`}</span>
        </div>
        <button class="addbutton btn btn-primary" onClick={() => setModalShow(true)}><i class="fas fa-plus-circle fa-2x"></i></button>
        <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
        id={id}
      />
    
    </div>
    <div class="container-fluid">
     <div class="row task-1">
      <div className="col-1">
        hello
        </div>
        <div className="col-4"> 
     </div> 
     <div className="col-5">
       </div>
       <div className="col-1">

       </div>
       <div className="col-1">
         
       </div>
   </div>
   </div>
</>
}

function MyVerticallyCenteredModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Task
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form onSubmit={
          (e)=>{
              e.preventDefault();
              console.log("function",props.id);
          }
        }>
        <div class="container-fluid row col-12">
     <label for="taskname" class="form-label">Task Name</label>
      <input type="text" class="form-control" id="taskname" />
     </div><br/>
     <div class="container-fluid row col-12">
     <label for="description" class="form-label">Description</label>
     <textarea class="form-control" id="description" rows="2"></textarea>
     </div><br/>
     <div class="container-fluid row  col-4">
     <label for="time" class="form-label">Time-(HH:mm-AM/PM)</label>
     <input type="time" class="form-control form-sm" min="00:00" max="23:59" id="time"  />
     </div><br/>
        <Modal.Footer>
        <button class="btn btn-primary" type="submit">Submit</button></Modal.Footer>
        </form>
      </Modal.Body>
    </Modal>
  );
}
