import React, { useState } from "react";
import Background from "./img/1489353.jpg";
import "./App.css";



export default function Login()
{    
    const[type,updatetype] = useState("password");
    const[eye,updateeye] = useState(<i class="far fa-eye"></i>);
    return <div style={{
        backgroundImage: `url(${Background})`,
        backgroundSize: "cover",
        height: "100vh",
        color: "#f5f5f5"
      }}
     class="container-fluid row">
        <div class="col-4 bg-primary">
          <form>
          <div class="container-fluid row">
              <div class="col-11">
          <label for="exampleFormControlInput1" class="form-label">Email address</label>
          <input type="email" class="form-control" id="exampleFormControlInput1" placeholder="name@example.com" required/>
          </div></div><br/>
          <div class="container-fluid row ">
              <div class="col-11">
              <label for="password" class="form-label">Password</label>
               <input type={type} class="form-control" id="password" required/>
              </div>
              <div class="co1-1">
                  <button class="btn btn-primary" onClick={
                      ()=>{
                          if(type=="password")
                          {
                              updatetype("text");
                              updateeye(<i class="fas fa-eye-slash"></i>)
                          }
                          else
                          {
                            updatetype("Password");
                            updateeye(<i class="far fa-eye"></i>) 
                          }
                      }
                  }>{eye}</button>
              </div>
          </div>
          </form>
        </div>
        </div>
}
